<script setup lang="ts">
import useClipboard from 'vue-clipboard3'
import appStore from "../../../store";

const { toClipboard } = useClipboard()
const copy = async (Msg: string, alert: string) => {
  try {
    // 复制
    await toClipboard(Msg)
    // 下面可以设置复制成功的提示框等操作
    appStore.counter.setDisplayName(alert)
    appStore.counter.setDisplay(true)
    setTimeout(() => {
      appStore.counter.setDisplay(false)
    }, 1500)
  } catch (e) {
    // 复制失败
    console.error(e)
  }
}
const copyEmail = () => {
  copy('2994498974@qq.com', '已复制邮箱信息到剪贴板！')
}
